sys_script_client.script:
//before this onload script : User logged details is running . 

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
 // when new value of request type is blank then return , means dont execute script 
	if ( newValue == '') {
      return;
   }
	// below else condition will show the value of current - newvalue , just for testing.
	//else{
	//	alert("it bypass 1st if and the new value is" + newValue);
	//}
	//Q1-why isloading is not takn in 1st if condition?- ans : because isloading will have 2 parts , 1-when //the form is loading and it is a new record to be created . 2- when the form is loaded and it is not the new //record/existing record opened . both cases shown below . 

	//Q2-why choice list of whatneeded field is taken into a seperate variable named what needed ? ans-becoz //if in case existing record is opened while form load, then the old value which is stored already in the //variable will diectly be called(check case 2) . 

	//take all the values of choice list of whatneeded in a variable named- whatneeded
	var whatneeded = g_form.getValue('u_what_needed');

	// case 1-when the form is loading and it is a new record to be created 

	// now clear all the choices of whatneeded choicelist
	g_form.clearOptions('u_what_needed');
	//now if the request type is selected as hr then add 2 matching options and other as a choicelist in whatneeded

	if(newValue == 'hr'){
		alert(g_form.getValue('u_request_type'));

		g_form.addOption('u_what_needed','hr1','Human Resources 1');
		g_form.addOption('u_what_needed','hr2','Human Resources 2');
		g_form.addOption('u_what_needed','other','other');
	}
   //now if the request type is selected as facilities then add 2 matching options and other as a choicelist in whatneeded
	if (newValue == 'facilities'){
		g_form.addOption('u_what_needed','facilities1','Facilities 1');
		g_form.addOption('u_what_needed','facilities2','Facilities 2');
		g_form.addOption('u_what_needed','other','other');
	}

	//now if the request type is selected as legal then add 2 matching options and other as a choicelist in //whatneeded

	if (newValue == 'legal'){
	g_form.addOption('u_what_needed','legal1','Legal 1');
		g_form.addOption('u_what_needed','lega2','Legal 2');
		g_form.addOption('u_what_needed','other','other');
}

//case 2:2- when the form is loaded and it is not the new record/existing record opened . 


	if (isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
